import global from 'global';
import path from 'path';
const {
  FEATURES = {},
  STORIES = []
} = global;

const autoTitleV2 = (meta, fileName) => {
  return meta.title;
};

const stripExtension = titleWithExtension => {
  let parts = titleWithExtension.split('/');
  const last = parts[parts.length - 1];
  const dotIndex = last.indexOf('.');
  const stripped = dotIndex > 0 ? last.substr(0, dotIndex) : last;
  parts[parts.length - 1] = stripped;
  const [first, ...rest] = parts;

  if (first === '') {
    parts = rest;
  }

  return parts.join('/');
};

export const autoTitleFromEntry = (fileName, entry) => {
  const {
    directory,
    titlePrefix = ''
  } = entry.specifier || {};

  if (fileName.startsWith(directory)) {
    const suffix = fileName.replace(directory, '');
    return stripExtension(path.join(titlePrefix, suffix));
  }

  return undefined;
};

const autoTitleV3 = (meta, fileName) => {
  if (meta.title) return meta.title;

  for (let i = 0; i < STORIES.length; i += 1) {
    const title = autoTitleFromEntry(fileName, STORIES[i]);
    if (title) return title;
  }

  return undefined;
};

export const autoTitle = FEATURES.previewCsfV3 ? autoTitleV3 : autoTitleV2;